import React, { useState } from "react";
import "../src/App.css";
import Form from "./components/Form";
import List from "./components/List";

const App = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(false); // Add the loading state

  const addUser = async (user) => {
    setIsLoading(true); // Start loading animation
    // Your logic to add the user here (you can keep the existing code)
    try {
      // Your logic to add the user
      setUsers([...users, user]); // Update the local state with the new user data

      // After adding the user, fetch the updated user list
      await fetchUsers();
    } catch (error) {
      console.error("Failed to add user:", error);
    }
    setIsLoading(false); // Stop loading animation after adding the user
  };

  const fetchUsers = async () => {
    // Implement the fetching logic here (similar to how it is done in the List component)
    try {
      const response = await fetch("http://localhost:5000/api/users", {
        method: "GET",
      });
      if (!response.ok) {
        throw new Error("Failed to fetch users: Server returned an error");
      }
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error("Failed to fetch users:", error);
    }
  };

  return (
    <div className="App">
      <h1>Event Manager</h1>
      <Form addUser={addUser} fetchUsers={fetchUsers} />

      <h2>Users List</h2>
      {isLoading ? (
        // Render the loading animation if isLoading is true
        <div className="wrapper">
          <div className="circle"></div>
          <div className="circle"></div>
          <div className="circle"></div>
          <div className="shadow"></div>
          <div className="shadow"></div>
          <div className="shadow"></div>
        </div>
      ) : (
        // Render the user list if isLoading is false
        <List users={users} fetchUsers={fetchUsers} />
      )}
    </div>
  );
};

export default App;
